import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import styles from './Header.module.css'; // Importe o módulo CSS

function Header({ atualizarSaldo }) {
  const [isLoading, setIsLoading] = useState(false);
  const [user, setUser] = useState(null);
  const [saldoAtual, setSaldoAtual] = useState(user ? user.saldo : 0);

  const idUser = 102;

  useEffect(() => {
    setIsLoading(true);
    axios
      .get(`http://localhost:8080/user/${idUser}`)
      .then(response => {
        setUser(response.data);
        setSaldoAtual(response.data.saldo);
        setIsLoading(false);
      })
      .catch(error => {
        console.log(error);
        setIsLoading(false);
      });
  }, []);

  return (
    <div id={styles.header}>
      <h1 id={styles.tituloHeader}>PompeuBet</h1>
      <p id={styles.saldo}>
        {user && user.saldo === 0 ? "Saldo zerado" : (user && `Saldo: R$ ${user.saldo} reais`)}
      </p>
      <ul id={styles.catalogo}>
        <li className={styles.homePage}>
          <Link className={styles.homePage} to="/">Home</Link>
        </li>
        <li className={styles.homePage}>
          <Link className={styles.homePage} to="/apostas">Apostas</Link>
        </li>
        <li className={styles.homePage}>
          <Link className={styles.homePage} to="/saquedeposito">Saque/Depósito</Link>
        </li>
        <li className={styles.homePage}>
          <Link className={styles.homePage} to="/perfil">Meu Perfil</Link>
        </li>
      </ul>
    </div>
  );
}

export default Header;
