                          </div>
                      </div>
                          <ReCAPTCHA
                            sitekey = {key}
                            onChange={onChangee}
                          />
                          {captchaIsDonee && <button
                              onClick={(e) => {
                              e.preventDefault()
                              fazerSaque(valorr)}}>Finalizar depósito
                          </button>}
                          {/* Fazer validação em que os inputs devem estar preenchidos */}                 
                  </form>  
                </>   
              )}             
        </div>
      </div>
    </div>
  );
}

