import React, { useState } from 'react';
import './RouletteWheel.module.css'; // Certifique-se de que o caminho do arquivo CSS está correto

function RouletteWheel() {
  const [rotation, setRotation] = useState(0);

  const spinWheel = () => {
    const randomDegrees = Math.floor(Math.random() * 360);
    setRotation(randomDegrees);
  };

  return (
    <div className="roulette-container">
      <div className="roulette-wheel" style={{ transform: `rotate(${rotation}deg)` }}>
        <div className="roulette-ball"></div>
      </div>
      <button onClick={spinWheel}>Spin</button>
    </div>
  );
}

export default RouletteWheel;
