import { MantineProvider, Box, Card, TextInput, Button } from "@mantine/core";
import { useState } from "react";
import axios from "axios";
import styles from './Home.module.css'; // Importando o m√≥dulo CSS

function Home() {
  const [usuario, setUsuario] = useState({
    usuario: "ss",
    senha: "dd",
  });
  const [showPassword, setShowPassword] = useState(false);

  const autenticar = () => {
    axios.post(`http://localhost:8080/usuario/cadastro`, usuario); // Corrigi a concatena√ß√£o da URL
  };

  return (
    <MantineProvider>
      <Box className={styles.container}>
        <Card className={styles.card} shadow="md" withBorder padding="md">
          <TextInput
            onChange={(e) => setUsuario({ ...usuario, usuario: e.target.value })}
            value={usuario.usuario}
            classNames={{ input: styles.input }} // Usando a classe do m√≥dulo CSS
          />
          <div className={styles.senhaContainer}>
            <TextInput
              type={showPassword ? "text" : "password"}
              onChange={(e) => setUsuario({ ...usuario, senha: e.target.value })}
              value={usuario.senha}
              classNames={{ input: styles.senha }} // Usando a classe do m√≥dulo CSS
            />
            <button
              type="button"
              className={styles.toggleButton}
              onClick={() => setShowPassword(!showPassword)}
            >
              {showPassword ? 'üôà' : 'üëÅÔ∏è'}
            </button>
          </div>
          <Button onClick={() => autenticar()}>Login</Button>
        </Card>
      </Box>
    </MantineProvider>
  );
}

export default Home;
