import { Link } from "react-router-dom"
import React, { useEffect, useState } from "react";
import axios from "axios";
import Loading from "../pages/Loading";

function Header () {

    const [isLoading, setIsLoading] = useState(false);
    const [user, setUser] = useState(null);

    const idUser = 102

    useEffect(() => {
      axios
        .get(`http://localhost:8080/user/${idUser}`)
        .then(response => {
          setUser(response.data);
          setIsLoading(false);
        })
        .catch(error => {
          console.log(error);
          setIsLoading(false);
        });
    }, []);

    if (isLoading) {
        return <Loading/>
      }

    return(
        <div id = "header">
            <h1 id = "tituloHeader">Urubu do Pix Investimentos</h1>
            <p>{user.saldo === 0 ? "Saldo zerado" : `Saldo atual: ${user.saldo}`}</p>
            <div id = "catalogo">
                <li className = "homePage">
                    <Link className = "homePage" to ="/">Home</Link>
                </li>
                <li className = "homePage">
                    <Link className = "homePage" to ="/urubudopix">Urubu do Pix</Link>
                </li>
                <li className = "homePage">
                    <Link className = "homePage" to ="/apostas">Apostas</Link>
                </li>
                <li className = "homePage">
                    <Link className = "homePage" to ="/saquedeposito">Saque/Dep√≥sito</Link>
                </li>
                <li className = "homePage">
                    <Link className = "homePage" to ="/perfil">Meu Perfil</Link>
                </li>
            </div>
        </div>
    )
}

export default Header
