import React, { useState } from 'react';
import { MantineProvider, Box, Card, TextInput, Button, Modal, Text } from '@mantine/core';
import axios from 'axios';
import styles from './Aposta.module.css';

function Apostas() {
  const [showForm, setShowForm] = useState(false);
  const [eventName, setEventName] = useState('');
  const [sport, setSport] = useState('');
  const [numBets, setNumBets] = useState(1);
  const [odds, setOdds] = useState(['']);
  const [showBetModal, setShowBetModal] = useState(false);
  const [description, setDescription] = useState('');
  const [odd, setOdd] = useState('');
  const [individualBets, setIndividualBets] = useState([]);

  const handleAddOdds = () => {
    setOdds([...odds, '']);
  };

  const handleAddBet = () => {
    const newBet = { description, odd };
    setIndividualBets([...individualBets, newBet]);
    setDescription('');
    setOdd('');
    setShowBetModal(false);
  };

  const handleSave = () => {
    const betDetails = {
      eventName,
      sport,
      numBets,
      odds,
      individualBets
    };

    // Implement your API call here
    axios.post('http://localhost:8080/api/create-bet', betDetails)
      .then(response => {
        // Handle success
        console.log('Bet created:', response.data);
      })
      .catch(error => {
        // Handle error
        console.error('Error creating bet:', error);
      });

    // Close the form and reset states
    setShowForm(false);
  };

  return (
    <MantineProvider>
      <Box className={styles.container}>
        <Card className={styles.card} shadow="md" withBorder padding="md">
          {!showForm ? (
            <Button onClick={() => setShowForm(true)} className={styles.button}>Criar Aposta</Button>
          ) : (
            <>
              <TextInput
                label="Nome do Evento"
                value={eventName}
                onChange={(e) => setEventName(e.target.value)}
                className={styles.input}
              />
              <TextInput
                label="Esporte"
                value={sport}
                onChange={(e) => setSport(e.target.value)}
                className={styles.input}
              />
              <TextInput
                label="Número de Apostas"
                type="number"
                value={numBets}
                onChange={(e) => setNumBets(Number(e.target.value))}
                className={styles.input}
              />
              {odds.map((odd, index) => (
                <TextInput
                  key={index}
                  label={`Chance ${index + 1}`}
                  value={odd}
                  onChange={(e) => {
                    const newOdds = [...odds];
                    newOdds[index] = e.target.value;
                    setOdds(newOdds);
                  }}
                  className={styles.input}
                />
              ))}
              <Button onClick={handleAddOdds} className={styles.button}>Adicionar Chance</Button>
              <Button onClick={() => setShowBetModal(true)} className={styles.button}>Cadastrar Aposta</Button>
              <Button onClick={handleSave} className={styles.button}>Confirmar Aposta</Button>
            </>
          )}
        </Card>

        {/* Modal for individual bet */}
        <Modal
          opened={showBetModal}
          onClose={() => setShowBetModal(false)}
          title="Cadastrar Aposta Individual"
        >
          <TextInput
            label="Descrição da Aposta"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            className={styles.input}
          />
          <TextInput
            label="Odd para Bater"
            value={odd}
            onChange={(e) => setOdd(e.target.value)}
            className={styles.input}
          />
          <Button onClick={handleAddBet} className={styles.button}>Adicionar Aposta</Button>
          <Button onClick={() => setShowBetModal(false)} className={styles.button}>Cancelar</Button>
        </Modal>
      </Box>
    </MantineProvider>
  );
}

export default Apostas;
