import React, { useState } from 'react';
import { MantineProvider, Box, Card, TextInput, Button, Modal, Text } from '@mantine/core';
import axios from 'axios';
import styles from './Aposta.module.css';

function Apostas() {
  const [eventName, setEventName] = useState('');
  const [sport, setSport] = useState('');
  const [numBets, setNumBets] = useState(1);
  const [odds, setOdds] = useState(['']);
  const [showModal, setShowModal] = useState(false);

  const handleAddOdds = () => {
    setOdds([...odds, '']);
  };

  const handleSave = () => {
    const betDetails = {
      eventName,
      sport,
      numBets,
      odds
    };

    // Implement your API call here
    axios.post('http://localhost:8080/api/create-bet', betDetails)
      .then(response => {
        // Handle success
        console.log('Bet created:', response.data);
      })
      .catch(error => {
        // Handle error
        console.error('Error creating bet:', error);
      });

    // Close the modal after saving
    setShowModal(false);
  };

  return (
    <MantineProvider>
      <Box className={styles.container}>
        <Card className={styles.card} shadow="md" withBorder padding="md">
          <TextInput
            label="Nome do Evento"
            value={eventName}
            onChange={(e) => setEventName(e.target.value)}
            className={styles.input}
          />
          <TextInput
            label="Esporte"
            value={sport}
            onChange={(e) => setSport(e.target.value)}
            className={styles.input}
          />
          <TextInput
            label="NÃºmero de Apostas"
            type="number"
            value={numBets}
            onChange={(e) => setNumBets(Number(e.target.value))}
            className={styles.input}
          />
          {odds.map((odd, index) => (
            <TextInput
              key={index}
              label={`Chance ${index + 1}`}
              value={odd}
              onChange={(e) => {
                const newOdds = [...odds];
                newOdds[index] = e.target.value;
                setOdds(newOdds);
              }}
              className={styles.input}
            />
          ))}
          <Button onClick={handleAddOdds} className={styles.button}>Adicionar Chance</Button>
          <Button onClick={() => setShowModal(true)} className={styles.button}>Criar Aposta</Button>
        </Card>

        <Modal
          opened={showModal}
          onClose={() => setShowModal(false)}
          title="Confirmar Aposta"
        >
          <Text>Deseja realmente criar esta aposta?</Text>
          <Button onClick={handleSave} className={styles.button}>Confirmar</Button>
          <Button onClick={() => setShowModal(false)} className={styles.button}>Cancelar</Button>
        </Modal>
      </Box>
    </MantineProvider>
  );
}

export default Apostas;
