import React, { useState } from 'react';
import { MantineProvider, Box, Card, TextInput, Button, Modal, Text } from '@mantine/core';
import axios from 'axios';
import styles from './Aposta.module.css';

function Apostas() {
  const [showForm, setShowForm] = useState(false);
  const [eventName, setEventName] = useState('');
  const [sport, setSport] = useState('');
  const [numBets, setNumBets] = useState(1);
  const [odds, setOdds] = useState(['']);
  const [showModal, setShowModal] = useState(false);
  const [showBetForm, setShowBetForm] = useState(false);
  const [betDescription, setBetDescription] = useState('');
  const [betOdd, setBetOdd] = useState('');

  const handleAddOdds = () => {
    setOdds([...odds, '']);
  };

  const handleSave = () => {
    const betDetails = {
      eventName,
      sport,
      numBets,
      odds
    };

    axios.post('http://localhost:8080/api/create-bet', betDetails)
      .then(response => {
        console.log('Bet created:', response.data);
      })
      .catch(error => {
        console.error('Error creating bet:', error);
      });

    setShowModal(false);
    setShowForm(false);
  };

  const handleAddBet = () => {
    setShowBetForm(true);
  };

  const handleSaveBet = () => {
    const betDetails = {
      description: betDescription,
      odd: betOdd
    };

    // Implement your API call here for adding bets
    axios.post('http://localhost:8080/api/add-bet', betDetails)
      .then(response => {
        console.log('Bet added:', response.data);
      })
      .catch(error => {
        console.error('Error adding bet:', error);
      });

    setShowBetForm(false);
  };

  return (
    <MantineProvider>
      <Box className={styles.container}>
        <Card className={styles.card} shadow="md" withBorder padding="md">
          {!showForm ? (
            <Button onClick={() => setShowForm(true)} className={styles.button}>Criar Aposta</Button>
          ) : (
            <>
              <TextInput
                label="Nome do Evento"
                value={eventName}
                onChange={(e) => setEventName(e.target.value)}
                className={styles.input}
              />
              <TextInput
                label="Esporte"
                value={sport}
                onChange={(e) => setSport(e.target.value)}
                className={styles.input}
              />
              <TextInput
                label="Número de Apostas"
                type="number"
                value={numBets}
                onChange={(e) => setNumBets(Number(e.target.value))}
                className={styles.input}
              />
              {odds.map((odd, index) => (
                <TextInput
                  key={index}
                  label={`Chance ${index + 1}`}
                  value={odd}
                  onChange={(e) => {
                    const newOdds = [...odds];
                    newOdds[index] = e.target.value;
                    setOdds(newOdds);
                  }}
                  className={styles.input}
                />
              ))}
              <Button onClick={handleAddOdds} className={styles.button}>Adicionar Chance</Button>
              <Button onClick={() => setShowModal(true)} className={styles.button}>Confirmar Aposta</Button>
              <Button onClick={handleAddBet} className={styles.button}>Adicionar Apostas</Button>
            </>
          )}
        </Card>

        <Modal
          opened={showModal}
          onClose={() => setShowModal(false)}
          title="Confirmar Aposta"
        >
          <Text>Deseja realmente criar esta aposta?</Text>
          <Button onClick={handleSave} className={styles.button}>Confirmar</Button>
          <Button onClick={() => setShowModal(false)} className={styles.button}>Cancelar</Button>
        </Modal>

        <Modal
          opened={showBetForm}
          onClose={() => setShowBetForm(false)}
          title="Adicionar Apostas"
        >
          <TextInput
            label="Descrição da Aposta"
            value={betDescription}
            onChange={(e) => setBetDescription(e.target.value)}
            className={styles.input}
          />
          <TextInput
            label="Odd para Bater"
            value={betOdd}
            onChange={(e) => setBetOdd(e.target.value)}
            className={styles.input}
          />
          <Button onClick={handleSaveBet} className={styles.button}>Salvar Aposta</Button>
          <Button onClick={() => setShowBetForm(false)} className={styles.button}>Cancelar</Button>
        </Modal>
      </Box>
    </MantineProvider>
  );
}

export default Apostas;
