import { Link } from "react-router-dom"
import React, { useEffect, useState } from "react";
import axios from "axios";

function Header() {
    const [isLoading, setIsLoading] = useState(false);
    const [user, setUser] = useState(null);

    const idUser = 102;

    const fetchUserData = () => {
        axios
            .get(`http://localhost:8080/user/${idUser}`)
            .then(response => {
                setUser(response.data);
                setIsLoading(false);
            })
            .catch(error => {
                console.log(error);
                setIsLoading(false);
            });
    };

    useEffect(() => {
        fetchUserData();
        const interval = setInterval(fetchUserData, 1000);

        return () => {
            clearInterval(interval); // Cleanup the interval when the component unmounts
        };
    }, []);



    return(
        <div id = "header">
            <h1 id = "tituloHeader">Urubu do Pix Investimentos</h1>
            <p id = "saldo">{user && user.saldo === 0 ? "Saldo zerado" : (user && `Saldo atual: R$ ${user.saldo} reais`)}</p>
            <div id = "catalogo">
                <li className = "homePage">
                    <Link className = "homePage" to ="/">Home</Link>
                </li>
                <li className = "homePage">
                    <Link className = "homePage" to ="/urubudopix">Urubu do Pix</Link>
                </li>
                <li className = "homePage">
                    <Link className = "homePage" to ="/apostas">Apostas</Link>
                </li>
                <li className = "homePage">
                    <Link className = "homePage" to ="/saquedeposito">Saque/Dep√≥sito</Link>
                </li>
                <li className = "homePage">
                    <Link className = "homePage" to ="/perfil">Meu Perfil</Link>
                </li>
            </div>
        </div>
    )
}

export default Header
