import React, { useState } from "react";
import { Button, Modal, TextInput, Select, NumberInput, Stack, Paper, Group } from "@mantine/core";
import styles from './Aposta.module.css';

function Apostas() {
  const [modalOpen, setModalOpen] = useState(false);
  const [eventName, setEventName] = useState('');
  const [sport, setSport] = useState('');
  const [numBets, setNumBets] = useState(1);
  const [odds, setOdds] = useState(['']);

  const handleAddOdds = () => {
    setOdds([...odds, '']);
  };

  const handleOddsChange = (index, value) => {
    const newOdds = [...odds];
    newOdds[index] = value;
    setOdds(newOdds);
  };

  const handleSubmit = () => {
    const betData = {
      eventName,
      sport,
      numBets,
      odds,
    };
    // Enviar betData para o backend
    console.log(betData);
    setModalOpen(false); // Fechar o modal após envio
  };

  return (
    <div>
      <Button onClick={() => setModalOpen(true)} className={styles.createButton}>
        Criar Aposta
      </Button>

      <Modal
        opened={modalOpen}
        onClose={() => setModalOpen(false)}
        title="Criar Nova Aposta"
        size="lg"
      >
        <Paper padding="lg" shadow="xs">
          <Stack spacing="md">
            <TextInput
              label="Nome do Evento"
              value={eventName}
              onChange={(e) => setEventName(e.target.value)}
              placeholder="Digite o nome do evento"
            />
            <Select
              label="Esporte"
              value={sport}
              onChange={(value) => setSport(value)}
              placeholder="Escolha um esporte"
              data={[
                { value: 'futebol', label: 'Futebol' },
                { value: 'basquete', label: 'Basquete' },
                { value: 'tenis', label: 'Tênis' },
                // Adicione mais esportes conforme necessário
              ]}
            />
            <NumberInput
              label="Número de Apostas"
              value={numBets}
              onChange={(value) => setNumBets(value)}
              min={1}
            />
            <div>
              <label>Chances de Cada Aposta:</label>
              {odds.map((odd, index) => (
                <TextInput
                  key={index}
                  value={odd}
                  onChange={(e) => handleOddsChange(index, e.target.value)}
                  placeholder={`Chances da aposta ${index + 1}`}
                  mb="sm"
                />
              ))}
              <Button onClick={handleAddOdds} variant="outline">
                Adicionar Chance
              </Button>
            </div>
            <Group position="right">
              <Button onClick={handleSubmit}>Criar Aposta</Button>
            </Group>
          </Stack>
        </Paper>
      </Modal>
    </div>
  );
}

export default Apostas;
