import { MantineProvider, Box, Card, TextInput, Button } from "@mantine/core";
import { useState } from "react";
import axios from "axios";
import styles from './Home.module.css';

function Home() {
  const [usuario, setUsuario] = useState({
    usuario: "ss",
    senha: "dd",
  });

  const autenticar = () => {
    axios.post(`http://localhost:8080/usuario/cadastro`, usuario);
  };

  return (
    <MantineProvider>
      <Box className={styles.container}>
        <Card className={styles.card} shadow="md" withBorder padding="md">
          <TextInput
            onChange={(e) => setUsuario({ ...usuario, usuario: e.target.value })}
            value={usuario.usuario}
            classNames={{ input: styles.input }}
            placeholder="Digite o usuÃ¡rio"
          />
          <TextInput
            type="password"
            onChange={(e) => setUsuario({ ...usuario, senha: e.target.value })}
            value={usuario.senha}
            classNames={{ input: styles.input }}
            placeholder="Digite a senha"
          />
          <Button classNamesonClick={() => autenticar()}>Login</Button>
        </Card>
      </Box>
    </MantineProvider>
  );
}

export default Home;
